import * as ts from 'typescript';

/**
 * 遍历节点
 * @param node 根节点
 * @param callback 回调函数
 */
export default function eachNode(node: ts.Node, callback: (childNode: ts.Node) => void): void {
  if (node) {
    callback(node);
    node.forEachChild((childNode) => {
      eachNode(childNode, callback);
    });
  }
}