import { Routes } from '../getAppRoutes';

export default function getFileNameByRoute(routes: Routes, path: string): string {
  const routesKeys = Object.keys(routes);

  for (let i = 0; i < routesKeys.length; i++) {
    const route = routes[routesKeys[i]];

    if (route.indexOf(path) >= 0) {
      return routesKeys[i];
    }
  }

  return '';
}