import * as fs from 'fs';
import * as path from 'path';

interface WriteParams {
  writeContent: string;
  filepath: string;
  filename: string;
}

export default function(writeParams: WriteParams, readFilePath?: string): void {
  const content = writeParams.writeContent || fs.readFileSync(path.resolve(__dirname, readFilePath as string), 'utf-8');
  fs.writeFileSync(writeParams.filepath + writeParams.filename, content, { encoding: 'utf8' });
}