import yj from '../typings';
import { History } from 'history';
import Page from './page';
// import { RouteComponentProps } from 'react-router';

export type AppRoute = (props: yj.PageProps<any>) => JSX.Element;

class Global {
  public appRoute!: AppRoute;
  public routes!: JSX.Element[];
  public history!: History;
  /** 页面id */
  public pageId: number = 1;
  /** repalce前的component */
  public replaceBeforeComponent!: Page<yj.PageProps<any>, any, any>;
  /** 是否处于input foucs状态，用于判断dispatch是否要优化 */
  public inputFoucs: boolean = false;
}

const global = new Global();

export default global;