import ObObject from '../../app/datatype/obObject';

class Calendar {
  /** 选中的日期 */
  public selectedDate: ObObject<Date| undefined> = new ObObject({ defaultValue: undefined });
  public currentDate: ObObject<Date> = new ObObject({ defaultValue: new Date() });
  public readonly index: ObObject<number> = new ObObject({ defaultValue: 0 });
}

export default Calendar;