import yj from '../../typings/index';
import * as React from 'react';
// import * as classNames from 'classnames';
// import svgRootPath from '../../utils/svgRootPath';
import './index.scss';
// const loadingSvg = svgRootPath + require('../../assets/loading.svg').id;

export interface Props extends yj.ComponentProps  {
  onClick?: yj.OnClick<SVGElement>;
  /** 图标链接地址 */
  src: string;
}

// const prefixCls = 'yj-component-icon';
const Icon = (props: Props) => {
  return (
    <svg
      className={props.className}
      style={props.style}
      onClick={props.onClick}
    >
      <use xlinkHref={props.src} />
    </svg>
  );
};

export default Icon;