import Fields, { BaseProps } from '../../_base/service/fields';
import ObObject from '../../app/datatype/obObject';

class Input extends Fields<string> {
  constructor(props: BaseProps<string>) {
    super(props);

    this.value = new ObObject({ defaultValue: props.defaultValue || '' });
  }

  public value: ObObject<string>;

  public clear(): void {
    this.value.set('');
    this.setPrompt('');
  }
}

export default Input;