/**
 * 获取滑动方向，通过计算索引差值来实现
 * @param index 当前索引
 * @param prevIndex 上一个索引
 * @param lastIndex 最后一个索引
 */
export default function getDirection(index: number, prevIndex: number, lastIndex: number): 'prev' | 'next' {
  if (index === 0 && prevIndex === lastIndex) {
    return 'next';
  } else if (index - prevIndex > 0) {
    return 'next';
  } else {
    return 'prev';
  }
}