import SearchParams from './urlsearchparams';

/** 获取是否已关注 */
export default function getIsSubscribe(): boolean {
  const urlsearchparams = new SearchParams(window.location.search);
  // tslint:disable-next-line:max-line-length
  const isSubscribe = urlsearchparams.get('isSubscribe') ||  urlsearchparams.get('?isSubscribe') || urlsearchparams.get('%3FisSubscribe') || urlsearchparams.get('%3fisSubscribe');

  return isSubscribe ? (isSubscribe === 'true' ? true : false) : false;
}