/** 获取url上的微信openId参数 */
export default function getOpenId(): string {
  const urlsearchparams = new URLSearchParams(window.location.search);
  // tslint:disable-next-line:max-line-length
  const openId = urlsearchparams.get('openId') ||  urlsearchparams.get('?openId') || urlsearchparams.get('%3FopenId') || urlsearchparams.get('%3fopenId');

  return openId || '';
}