export default function hexToString(hex: string): string {
  const arr = hex.split('');
  let out = '';

  for (let i = 0; i < arr.length / 2; i++) {
    const tmp = parseInt('0x' + arr[i * 2] + arr[i * 2 + 1], 16);
    const charValue = String.fromCharCode(tmp);

    out += charValue;
  }

  return decodeURIComponent(out);
}