let SearchParams: Window['URLSearchParams'];
// IOS上直接使用URLSearchParams可能会出错
try {
  SearchParams = URLSearchParams || require('url-search-params') as Window['URLSearchParams'];
} catch (err) {
  SearchParams = require('url-search-params') as Window['URLSearchParams'];
}

window.URLSearchParams = SearchParams;

export default SearchParams;